      <span style={{ display: 'inline-flex', alignItems: 'center', gap: 2 }}>
        {Array.from({ length: 5 }, (_, i) => {
          if (i < fullStars) return <span key={i}>{starSvg('#fbbf24')}</span>;
          if (i === fullStars && hasHalfStar) {
            return (
